document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("steps");if(e){const t=e.getBoundingClientRect().top+document.documentElement.scrollTop;e.classList.add("steps--sticky"),window.addEventListener("scroll",()=>{window.pageYOffset>t?setTimeout(()=>{e.classList.add("steps--fixed")},300):setTimeout(()=>{e.classList.remove("steps--fixed")},300)});const s=e.querySelectorAll("a"),o=new IntersectionObserver(t=>{t.forEach(t=>{const o=function(t){return console.log(t),e.querySelector(`a[href="#${t}"]`)}(t.target.id);t.isIntersecting&&(s.forEach(e=>{e.classList.remove("steps__link--active")}),o.classList.add("steps__link--active"))})},{root:null,rootMargin:"-50%"});s.forEach(e=>{const t=e.getAttribute("href");if(!t)return;const s=document.querySelector(t);s&&o.observe(s)})}});